import TinderCard from 'react-tinder-card'
import { useEffect, useState } from 'react'
import ChatContainer from '../components/ChatContainer'
import { useCookies } from 'react-cookie'
import axios from 'axios'

const Dashboard = () => {
    const [user, setUser] = useState(null)
    const [fieldofUsers, setfieldofUsers] = useState(null)
    const [lastDirection, setLastDirection] = useState()
    const [cookies] = useCookies(['user'])

    const userId = cookies.UserId


    const getUser = async () => {
        try {
            const response = await axios.get('http://localhost:8000/user', {
                params: { userId }
            })
            setUser(response.data)
            console.log("getting USERRR")
        } catch (error) {
            console.log(error)
        }
    }
    const getfieldofUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8000/field-users', {
                params: { field: user?.field }
            })
            setfieldofUsers(response.data)
            console.log("getting fielding USERRR", response.data)
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        getUser()
        //eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    useEffect(() => {
        if (user) {
            getfieldofUsers()
        }
    }, [user]);


    const updateMatches = async (matchedUserId) => {
        try {
            await axios.put('http://localhost:8000/addmatch', {
                userId,
                matchedUserId
            })
            getUser()
            console.log("getting updated matched USERRR")
        } catch (err) {
            console.log(err)
        }
    }

    const swiped = (direction, swipedUserId) => {
        if (direction === 'right') {
            updateMatches(swipedUserId)
        }
        setLastDirection(direction)
    }

    const outOfFrame = (name) => {
        console.log(name + ' left the screen!')
    }

    const matchedUserIds = user?.matches.map(({ user_id }) => user_id).concat(userId)

    const filteredUsers = fieldofUsers?.filter(fieldofUser => !matchedUserIds.includes(fieldofUser.user_id))


    console.log('filteredGenderedUsers ', filteredUsers)
    return (
        <>
            {user &&
                <div className="dashboard">
                    <ChatContainer user={user} />
                    <div className="swipe-container">
                        <div className="card-container">

                            {filteredUsers?.map((fieldofUsers) =>
                                <TinderCard
                                    className="swipe"
                                    key={fieldofUsers.user_id}
                                    preventSwipe={["up", "down"]}
                                    onSwipe={(dir) => swiped(dir, fieldofUsers.user_id)}
                                    onCardLeftScreen={() => outOfFrame(fieldofUsers.name)}>
                                    <div
                                        style={{ backgroundImage: "url(" + fieldofUsers.url + ")" }}
                                        className="card">
                                        <h3>{fieldofUsers.name}</h3>
                                    </div>
                                </TinderCard>
                            )}
                            <div className="swipe-info">
                                {lastDirection ? <p>You swiped {lastDirection}</p> : <p />}
                            </div>
                        </div>
                    </div>
                </div>}
        </>
    )
}
export default Dashboard
